  &lt;r:version&gt;1.6.1&lt;/r:version&gt;
  &lt;r:status&gt;success&lt;/r:status&gt;
  &lt;r:repository&gt;https://ropensci.r-universe.dev&lt;/r:repository&gt;
  &lt;r:upstream&gt;https://github.com/ropensci/cld3&lt;/r:upstream&gt;
